mapper.insert:
delete
from budget
where userid = #{userid}
  and exists(select * from budget where userid = #{userid});
insert into budget(userid, budget)
VALUES (#{userid}, #{budget})

mapper.update:
update accounttb
typename=#{typename}, sImageId=#{sImageId}, beizhu=#{beizhu}, money=#{money}, time=#{time}, year=#{year}, month=#{month}, day =#{day}, kind=#{kind}
where id =#{id} and userid=#{userid}

mapper.delete:
delete
from accounttb
where id = #{id}
  and userid = #{userid}

mapper.select:
select *
from accounttb
where year = #{year}
  and month = #{month}
  and day = #{day}
  and userid = #{userid}
order by id desc

select *
from accounttb
where year = #{year}
  and month = #{month}
  and userid = #{userid}
order by id desc

select sum(money) as summoney
from accounttb
where year = #{year}
  and month = #{month}
  and day = #{day}
  and kind = #{kind}
  and userid = #{userid}

select sum(money)
from accounttb
where year = #{year}
  and month = #{month}
  and kind = #{kind}
  and userid = #{userid}

select count(money)
from accounttb
where year = #{year}
  and month = #{month}
  and kind = #{kind}
  and userid = #{userid}

select sum(money)
from accounttb
where year = #{year}
  and kind = #{kind}
  and userid = #{userid}

select typename, sImageId, sum(money) as total
from accounttb
where year = #{year}
  and month = #{month}
  and kind = #{kind}
  and userid = #{userid}
group by typename, simageid
order by total desc

select sum(money)
from accounttb
where year = #{year}
  and month = #{month}
  and kind = #{kind}
  and userid = #{userid}
group by day
order by sum(money) desc

select day, sum(money) as summoney
from accounttb
where year = #{year}
  and month = #{month}
  and kind = #{kind}
  and userid = #{userid}
group by day

select *
from accounttb
where beizhu like #{beizhu}
  and userid = #{userid}

select budget
from budget
where userid = #{userid}

select *
from accounttb
where typename = #{type}
  and userid = #{userid}
